function burgerMenu(){const e=document.querySelector(".burger"),s=document.querySelector(".menu"),r=document.querySelector("body");e.addEventListener("click",(()=>{s.classList.contains("active")?(s.classList.remove("active"),e.classList.remove("active-burger"),r.classList.remove("locked")):(s.classList.add("active"),e.classList.add("active-burger"),r.classList.add("locked"))}));document.querySelectorAll(".menu__item").forEach((i=>{i.addEventListener("click",(function(){s.classList.remove("active"),e.classList.remove("active-burger"),r.classList.remove("locked")}))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(s.classList.remove("active"),e.classList.remove("active-burger"),r.classList.remove("locked"))}))}function fixedNav(){const e=document.querySelector("nav");window.scrollY>=1?e.classList.add("fixed__nav"):e.classList.remove("fixed__nav")}burgerMenu(),window.addEventListener("scroll",fixedNav);var swiper=new Swiper(".partners__slider",{slidesPerView:1,spaceBetween:10,loop:!0,navigation:{nextEl:".partners__slider-arrow-next",prevEl:".partners__slider-arrow-prev"},breakpoints:{320:{slidesPerView:1,spaceBetween:20},767:{slidesPerView:2,spaceBetween:40},1024:{slidesPerView:3,spaceBetween:80}}});